--
-- Table structure for table `countries`
--

CREATE TABLE IF NOT EXISTS `countries` (
  `id` smallint(4) NOT NULL AUTO_INCREMENT,
  `name` varchar(80) NOT NULL,
  `full_name` varchar(80) DEFAULT NULL,
  `iso2` char(2) DEFAULT NULL COMMENT 'ISO alpha-2 code',
  `iso3` char(3) DEFAULT NULL COMMENT 'ISO alpha-3 code',
  `continent_name` varchar(24) DEFAULT NULL,
  `currency_name` varchar(32) DEFAULT NULL,
  `currency_code` char(3) DEFAULT NULL,
  `currency_symbol` varchar(3) DEFAULT NULL,
  `calling_code` varchar(8) DEFAULT NULL COMMENT 'International dialing code',
  `numeric_code` varchar(6) DEFAULT NULL COMMENT 'United Nations numerical code M49 for countries',
  `postal_code` varchar(45) DEFAULT NULL COMMENT 'Pattern for postal code of the country.',
  `un_member` varchar(12) DEFAULT NULL,
  `cctld` varchar(5) DEFAULT NULL COMMENT 'country code top-level domain',
  `flag` varchar(6) DEFAULT NULL COMMENT 'Flag image name with extension',
  `is_active` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'Active = 1, Inactive = 0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `countries_uq_name` (`name`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

